parameters.jsCode:
// Get response data
const items = $input.all();
const item = items[0];

// Check if there was an error (from error output)
const hasError = item.json.error !== undefined;

let status, statusCode, responseTime, errorMessage;

if (hasError) {
  status = 'DOWN';
  statusCode = 0;
  responseTime = 0;
  errorMessage = item.json.error?.message || 'Connection failed';
} else {
  statusCode = item.json.statusCode || 0;
  responseTime = item.json.responseTime || 0;

  // Define thresholds
  const MAX_RESPONSE_TIME = 5000; // 5 seconds

  if (statusCode >= 200 && statusCode < 400 && responseTime < MAX_RESPONSE_TIME) {
    status = 'UP';
    errorMessage = null;
  } else if (statusCode >= 400) {
    status = 'DOWN';
    errorMessage = `HTTP Error: ${statusCode}`;
  } else if (responseTime >= MAX_RESPONSE_TIME) {
    status = 'SLOW';
    errorMessage = `Response time: ${responseTime}ms exceeds ${MAX_RESPONSE_TIME}ms`;
  } else {
    status = 'UNKNOWN';
    errorMessage = 'Unknown status';
  }
}

return [{
  json: {
    url: $vars.MONITOR_URL || 'https://example.com',
    status,
    statusCode,
    responseTime,
    errorMessage,
    checkedAt: DateTime.now().toISO(),
    shouldAlert: status !== 'UP'
  }
}];

